import React, { useEffect, useState } from 'react';
import { useDispatch, useSelector } from 'react-redux';
import { setPomo } from '../redux/pomo';
import styled from 'styled-components';

const TimerContainer = styled.div`
  text-align: center;
  font-size: 5vw;
`;

const PomoButton = styled.button`
  background-color: tomato;
  border-radius: 10px;
  color: white;
  border: none;
  font-weight: bold;
  cursor: pointer;
  margin: 1vw;
  padding: 10px;
`;

const PomoStopButton = styled.button`
  background-color: rgb(16, 134, 70);
  border-radius: 10px;
  color: white;
  border: none;
  font-weight: bold;
  cursor: pointer;
  margin: 1vw;
  padding: 10px;
`;

const FinishStudyButton = styled.button`
  background-color: black;
  border-radius: 10px;
  color: white;
  border: none;
  font-weight: bold;
  cursor: pointer;
  margin: 1vw;
  padding: 10px;
`;

const Timer = () => {
  const [minutes, setMinutes] = useState(25);
  const [seconds, setSeconds] = useState(0);
  const [start, setStart] = useState(false); // 시작버튼을 눌렀는지 관리하는 state
  const [rest, setRest] = useState(false); // 공부하는 타임인지 휴식하는 타임인지 관리하는 state
  //   const secondsRef = useRef(0);
  // const data = useSelector(state => state.pomo.value);
  const pomo = useSelector(state => state.pomo.value.pomoNum);
  const dayPomo = useSelector(state => state.pomo.value.dayPomo);

  const dispatch = useDispatch();
  const bgm = new Audio(
    'data:audio/wav;base64,',
  );
  const startTimer = () => {
    setStart(true);
    // bgm.play();
  };
  const stopTimer = () => {
    setStart(false);
    // bgm.play();
  };
  const increasePomo = () => {
    dispatch(setPomo({ pomoNum: [...pomo, 1], dayPomo: dayPomo }));
    bgm.play();
  };
  const increaseDayPomo = () => {
    setStart(false);
    setMinutes(25);
    setSeconds(0);
    dispatch(
      setPomo({
        pomoNum: [],
        dayPomo: [...dayPomo, pomo.length],
      }),
    );

    // console.log(data);
  };
  useEffect(() => {
    const countdown = setInterval(() => {
      if (start) {
        if (rest) {
          if (parseInt(seconds) === 0) {
            if (parseInt(minutes) > 0) {
              setSeconds(59);
              setMinutes(parseInt(minutes) - 1);
            } else {
              // 휴식 끝
              setRest(false);
              setMinutes(25);
              increasePomo(); // 휴식이 끝나면 뽀모 갯수 증가
              clearInterval(countdown);
            }
          }
          if (parseInt(seconds) > 0) {
            setSeconds(parseInt(seconds) - 1);
          }
        } else {
          if (parseInt(seconds) === 0) {
            if (parseInt(minutes) > 0) {
              setSeconds(59);
              setMinutes(parseInt(minutes) - 1);
            } else {
              // 휴식 시작
              bgm.play();
              setRest(true);
              if (pomo.length !== 0 && pomo.length % 4 === 0) {
                setMinutes(30); // 4세트마다 (포모 갯수가 4개가 되면 30분씩 휴식시간 부여)
              } else {
                setMinutes(5);
              }
              clearInterval(countdown);
            }
          }
          if (parseInt(seconds) > 0) {
            setSeconds(parseInt(seconds) - 1);
          }
        }
      }
    }, 1000);
    return () => {
      clearInterval(countdown);
    };
  }, [start, minutes, seconds, rest]);
  return (
    <TimerContainer>
      <div>
        {rest ? '휴식 시간' : '공부 시간'}
        {minutes}:{seconds}
      </div>

      <PomoButton onClick={startTimer}>뽀모 시작</PomoButton>
      <PomoStopButton onClick={stopTimer}>타이머 일시정지</PomoStopButton>
      <FinishStudyButton onClick={increaseDayPomo}>
        공부 마치기
      </FinishStudyButton>
    </TimerContainer>
  );
};

export default Timer;
